package modules;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 10; i++) {
			for (int j = 1; j <= 5; j++) {
				System.out.print("\t" + j + " * " + i + " = " + i * j);

			}
			System.out.println();

		}

	}

}
